mapper.insert:
INSERT IGNORE INTO news(id, title, url, catLabel2, recommend, updatetime) values

	(
		#{news.id},#{news.title},#{news.url},#{news.catLabel2},#{news.recommend},#{news.updatetime}
	)

mapper.update:
update news SET 
	title=#{title},
	updatetime=#{updatetime},
	recommend=#{recommend}
where 
	id = #{id}
